import { Zap, Clock, Skull } from "lucide-react";
import { Button } from "@/components/ui/button";
import { type Difficulty, difficultyConfig } from "@shared/schema";
import { cn } from "@/lib/utils";

interface DifficultySelectorProps {
  selected: Difficulty;
  onSelect: (difficulty: Difficulty) => void;
  completedDifficulties?: Difficulty[];
  disabled?: boolean;
  compact?: boolean;
}

const difficultyData: { id: Difficulty; label: string; labelUa: string; icon: typeof Zap }[] = [
  { id: "easy", label: "EASY", labelUa: "ЛЕГКИЙ", icon: Zap },
  { id: "medium", label: "MEDIUM", labelUa: "СЕРЕДНІЙ", icon: Clock },
  { id: "hard", label: "HARD", labelUa: "ВАЖКИЙ", icon: Skull },
];

export function DifficultySelector({ 
  selected, 
  onSelect, 
  completedDifficulties = [],
  disabled = false,
  compact = false
}: DifficultySelectorProps) {
  return (
    <div className={cn(
      "flex gap-2",
      compact ? "flex-row" : "flex-col sm:flex-row"
    )}>
      {difficultyData.map(({ id, label, labelUa, icon: Icon }) => {
        const config = difficultyConfig[id];
        const isSelected = selected === id;
        const isCompleted = completedDifficulties.includes(id);

        const colorClasses = {
          easy: isSelected 
            ? "bg-green-500/20 border-green-500 text-green-400 shadow-green-500/20 shadow-lg" 
            : "border-green-500/30 text-green-400/70 hover:border-green-500/60",
          medium: isSelected 
            ? "bg-yellow-500/20 border-yellow-500 text-yellow-400 shadow-yellow-500/20 shadow-lg" 
            : "border-yellow-500/30 text-yellow-400/70 hover:border-yellow-500/60",
          hard: isSelected 
            ? "bg-red-500/20 border-red-500 text-red-400 shadow-red-500/20 shadow-lg" 
            : "border-red-500/30 text-red-400/70 hover:border-red-500/60",
        };

        return (
          <Button
            key={id}
            variant="outline"
            onClick={() => onSelect(id)}
            disabled={disabled}
            data-testid={`button-difficulty-${id}`}
            className={cn(
              "relative flex-1 border-2 transition-all duration-300 font-mono",
              colorClasses[id],
              compact ? "px-3 py-2" : "px-4 py-6",
              isCompleted && "ring-2 ring-primary/50"
            )}
          >
            <div className={cn(
              "flex items-center gap-2",
              compact ? "flex-row" : "flex-col"
            )}>
              <Icon className={cn("transition-transform", isSelected && "scale-110", compact ? "h-4 w-4" : "h-5 w-5")} />
              <div className={cn("flex flex-col", compact && "hidden sm:flex")}>
                <span className={cn("font-bold tracking-wider", compact ? "text-xs" : "text-sm")}>{labelUa}</span>
                {!compact && (
                  <span className="text-xs opacity-70">
                    {config.hintDelay}s • x{config.multiplier}
                    {config.timeLimit && ` • ${config.timeLimit / 60}m`}
                  </span>
                )}
              </div>
              {isCompleted && (
                <div className="absolute -top-1 -right-1 w-3 h-3 bg-primary rounded-full" />
              )}
            </div>
          </Button>
        );
      })}
    </div>
  );
}

server/storage.ts.content:
import { type Player, type InsertPlayer, type PlayerProgress, type Difficulty } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getPlayer(id: string): Promise<Player | undefined>;
  getPlayerByNickname(nickname: string): Promise<Player | undefined>;
  createPlayer(player: InsertPlayer): Promise<Player>;
  updatePlayerProgress(playerId: string, progress: PlayerProgress): Promise<Player | undefined>;
  updatePlayerScore(playerId: string, scoreToAdd: number): Promise<Player | undefined>;
  addAchievement(playerId: string, achievementId: string): Promise<Player | undefined>;
  getAllPlayers(): Promise<Player[]>;
}

export class MemStorage implements IStorage {
  private players: Map<string, Player>;

  constructor() {
    this.players = new Map();
  }

  async getPlayer(id: string): Promise<Player | undefined> {
    return this.players.get(id);
  }

  async getPlayerByNickname(nickname: string): Promise<Player | undefined> {
    return Array.from(this.players.values()).find(
      (player) => player.nickname.toLowerCase() === nickname.toLowerCase(),
    );
  }

  async createPlayer(insertPlayer: InsertPlayer): Promise<Player> {
    const id = randomUUID();
    const player: Player = { 
      id, 
      nickname: insertPlayer.nickname,
      totalScore: 0,
      levelsCompleted: 0,
      achievements: [],
      progress: {},
    };
    this.players.set(id, player);
    return player;
  }

  async updatePlayerProgress(playerId: string, progress: PlayerProgress): Promise<Player | undefined> {
    const player = this.players.get(playerId);
    if (!player) return undefined;

    const key = `${progress.levelId}-${progress.difficulty}`;
    const existingProgress = player.progress[key];

    const updatedPlayer: Player = {
      ...player,
      progress: {
        ...player.progress,
        [key]: {
          ...progress,
          score: existingProgress ? Math.max(existingProgress.score, progress.score) : progress.score,
        },
      },
    };

    const completedLevels = new Set(
      Object.values(updatedPlayer.progress)
        .filter(p => p.completed)
        .map(p => p.levelId)
    );
    updatedPlayer.levelsCompleted = completedLevels.size;

    this.players.set(playerId, updatedPlayer);
    return updatedPlayer;
  }

  async updatePlayerScore(playerId: string, scoreToAdd: number): Promise<Player | undefined> {
    const player = this.players.get(playerId);
    if (!player) return undefined;

    const updatedPlayer: Player = {
      ...player,
      totalScore: player.totalScore + scoreToAdd,
    };

    this.players.set(playerId, updatedPlayer);
    return updatedPlayer;
  }

  async addAchievement(playerId: string, achievementId: string): Promise<Player | undefined> {
    const player = this.players.get(playerId);
    if (!player) return undefined;

    if (player.achievements.includes(achievementId)) {
      return player;
    }

    const updatedPlayer: Player = {
      ...player,
      achievements: [...player.achievements, achievementId],
    };

    this.players.set(playerId, updatedPlayer);
    return updatedPlayer;
  }

  async getAllPlayers(): Promise<Player[]> {
    return Array.from(this.players.values());
  }
}

export const storage = new MemStorage();

